import React, { Component } from 'react'
import { getWordList } from '../../api/poem'

// style blocks
const panel = {
  position: 'absolute',
  right: '10px',
  width: '200px',
  height: '500px',
  border: '1px solid #000',
  padding: '2px'
}
const position = {
    position: 'absolute',
    right: '50px',
    top: '100px'
}

class WordPanel extends Component {
    constructor (props) {
        super(props)

        this.state = {
        wordList: null
        }
    }

    componentDidMount () {
        this.generateWordList()
    }

    render () {
    // variable to hold the state
    const { wordList } = this.state

    let wordListJSX
    if (wordList === null) {
        return 'Loading...'
    }else{
        wordListJSX = wordList.map((word) => (
            <div key={word}>
            <h3>{word}</h3>
            </div>
        ))
    }
    return (
        <>
        <h3 style={position}>Todays Words</h3>
            <div style={panel}>
                eggs
                <ul>{wordListJSX}</ul>
            </div>
        </>
    )
    }
}

export default WordPanel
