import React, { Component } from 'react'
import { getWordList } from '../../api/poem'
import { withRouter, Link } from 'react-router-dom'

// style blocks
const panel = {
  position: 'absolute',
  top: '100px',
  right: '10px',
  width: '200px',
  height: '970px',
  border: '1px solid #000',
  padding: '2px'
}
const position = {
    position: 'absolute',
    right: '50px',
    top: '100px'
}

class WordPanel extends Component {
    constructor (props) {
        super(props)

        this.state = {
        wordList: null
        }
    }

    componentDidMount () {
        const { user, msgAlert, history } = this.props

        getWordList(user)
            .then((res) => this.setState({wordList: res.data}))
            .then(() => {
                msgAlert({
                    heading: 'Word List Generated',
                    message: 'Woo Created',
                    variant: 'success'
                })
            })
            .catch((error) => {
                msgAlert({
                    heading: 'Get Words Fail',
                    message: 'Poem error: ' + error.message,
                    variant: 'danger'
                })
            })
    }

    render () {
    // variable to hold the state
    const { wordList } = this.state

    let wordListJSX
    if (wordList === null) {
        return 'Loading...'
    }else{
        wordListJSX = wordList.map((word) => (
            <div key={word}>
                <Link to={`/poems/${user._id}`}>
                    <li>{word}</li>
                </Link>
            </div>
        ))
    }
    return (
        <>
        {/* <h3 style={position}>Todays Words</h3> */}
            <div style={panel}>
                <ul>{wordListJSX}</ul>
            </div>
        </>
    )
    }
}

export default WordPanel
