import React, { Component } from 'react'
import { getWordList } from '../../api/poem'

// style blocks
const panel = {
  position: 'absolute',
  right: '10px',
  width: '200px',
  height: '500px',
  border: '1px solid #000',
  padding: '2px'
}
const position = {
    position: 'absolute',
    right: '50px',
    top: '100px'
}

class WordPanel extends Component {
    constructor (props) {
        super(props)

        this.state = {
        wordList: []
        }
    }

    componentDidMount () {
    }



    getWordList () {
        const { user, msgAlert, history } = this.props

	createPoem(this.state, user)
		.then(() => history.push('/poems'))
		.then(() => {
			msgAlert({
				heading: 'New Poem Created',
				message: 'Woo Created',
				variant: 'success'
			})
		})
		.catch((error) => {
			msgAlert({
				heading: 'Poem Creation Fail',
				message: 'Poem error: ' + error.message,
				variant: 'danger'
			})
		})
    }

    render () {
    // variable to hold the state
    const { wordList } = this.state

    if (wordList === null) {
        return 'Loading...'
    }
        let wordListJSX
        wordListJSX = wordList.map((word) => (
            <div key={word}>
            <h3>{word}</h3>
            <>
                <button type="button" className="btn btn-light" >Add Word</button>
            </>
            </div>
        ))

    return (
        <>
        <h3 style={position}>Todays Words</h3>
            <div style={panel}>
                eggs
                <ul>{wordListJSX}</ul>
            </div>
        </>
    )
    }
}

export default WordPanel
