import React, { Component } from 'react'
import { Link } from 'react-router-dom'


class MyPoems extends Component {
  constructor (props) {
    super(props)

    this.state = {
      poems: null
    }
  }

  componentDidMount () {
    const { user, msgAlert } = this.props

    indexPosts(user)
      .then((res) => this.setState({ poems: res.data.posts }))
      .then(() => {
        msgAlert({
          heading: 'Welcome to <30',
          message: 'Look at allll those Poems',
          variant: 'success'
        })
      })
      .catch((error) => {
        msgAlert({
          heading: 'Index Failed',
          message: 'Index error:' + error.message,
          variant: 'danger'
        })
      })
  }


render () {
  const { poems } = this.state

  if (poems === null) {
    return 'Loading...'
  }

  let postJSX
  if (poems.length === 0) {
    postJSX = 'No poems, create some'
  } else {
    // eslint-disable-next-line array-callback-return
    postJSX = posts.map((post) => (
      <div key={post._id}>
        <h1>{post.owner?.username}</h1>
        <h5>{post.title}</h5>
        <p>{post.text}</p>
        <>
          <Link to={`/posts/${post._id}`}><button type="button" className="btn btn-light" >View Post</button></Link>
        </>
      </div>
    )).reverse()
  }
  return (
    <>
      <h3>Bubble Feed</h3>
      <ul>{postJSX}</ul>

    </>
  )
}
}

export default IndexPost
