import apiUrl from '../apiConfig'
import axios from 'axios'

export const createPost = (data, user) => {
  return axios({
    method: 'POST',
    url: apiUrl + '/poems/',
    data: {
      poem: data
    },
    headers: {
      Authorization: `Token ${user.token}`
    }
  })
}

export const indexPoems = (user) => {
  return axios({
    method: 'GET',
    url: apiUrl + '/poems/',
    headers: {
      Authorization: `Token ${user.token}`
    }
  })
}

export const showPost = (id, user) => {
  return axios({
    method: 'GET',
    url: apiUrl + '/posts/' + id,
    headers: {
      Authorization: `Bearer ${user.token}`
    }
  })
}
