import React, { Component } from 'react'

// style blocks
const panel = {
  position: 'absolute',
  right: '10px',
  width: '200px',
  height: '500px',
  border: '1px solid #000',
  padding: '2px'
}
const position = {
    position: 'absolute',
    right: '50px',
    top: '100px'
}

class WordPanel extends Component {
    constructor (props) {
        super(props)

        this.state = {
        wordList: []
        }
    }

    componentDidMount () {
    }

    // print random amount of words from a text file: https://stackoverflow.com/questions/56243621/print-random-amount-of-words-from-a-text-file

    randomWords(){
        // declare 'require()' imports at the top of your script
    fs = require('fs');
    // Since you use 'readFileSync', there is no callback function
    var readME = fs.readFileSync('lessthan30_frontend/public/wordlist10000.txt', 'utf8');

    // Split the string into an array
    var wordArr = readME.split(',');

    // Create empty array
    var randWords = [];

    // push a random word to the new array n times
    for (let i = 0; i < 30; i++){

        // new random number
        let newRandom;
        do {
            // New random index based on the length of the array (word count)
            let rand = Math.floor(Math.random() * wordArr.length);
            newRandom = wordArr[rand];
        }
        // Make sure we don't have duplicates
        while (randWords.includes(newRandom));

        // Add the new word to the array
        randWords.push(newRandom);
    }
    // Join the array to a string and return it
    return randWords
}


    render () {
    // variable to hold the state 
    const { wordList } = this.state

    if (wordList === null) {
        return 'Loading...'
    }
        let wordListJSX
        wordListJSX = wordList.map((word) => (
            <div key={word}>
            <h3>{word}</h3>
            <>
                <button type="button" className="btn btn-light" >Add Word</button>
            </>
            </div>
        ))

    return (
        <>
        <h3 style={position}>Todays Words</h3>
            <div style={panel}>
                eggs
                <ul>{wordListJSX}</ul>
            </div>
        </>
    )
    }
}

export default WordPanel
